    // 黑板(投影方式)
    const projection = geoMercator()
      .center([107, 31])
      .scale(900)
      .translate([SIZE.width / 2, SIZE.height / 2]);

    // 地理路径生成器
    const path = geoPath().projection(projection);

    /***** 2.画地图 *****/
